mapper.select:

    select * from (

select
    s.solution_id,
    s.problem_id,
    s.user_id,s.contest_id,
    s.time,
    s.memory,
    s.score,
    s.language,
    s.code_length,
    s.in_date
from solution s

     and contest_id=#{contest}
     and problem_id=#{problem}
     and user_id=#{user}
     and language=#{language}
     and score=#{score}
     and solution_id>#{bottom}
     and solution_id<#{top}

order by solution_id
desclimit #{size}
) tmp order by id desc

select score,count(*) count
from solution
where problem_id=#{problemId}
 and contest_id = #{contestId}
group by score
